  WON,
  LOST,
  IN_PROGRESS,
}

type IGameStatus = {
  gameOver: boolean;
  state: GameState;
};

export type IStoreState = {
  answer: string;
  alreadyGuessed: IGuess[];
  keysPressed: Record<string, LetterState>;
  addGuess: (guess: string) => void;
  gameStatus: IGameStatus;
  newGame: () => void;
};

