import React, { useState } from "react";
import "./App.css";
import HelpIcon from "./components/icons/HelpIcon";
import StatisticIcon from "./components/icons/StatisticIcon";
import HelpModal from "./components/modals/HelpModal";
import StatisticModal from "./components/modals/StatisticModal";

function App() {
  const [isHelpModalOpen, setIsHelpModalOpen] = useState(false);
  const [isStatisticModalOpen, setIsStatisticHelpModalOpen] = useState(false);

  const openHelpModal = () => {
    setIsHelpModalOpen(true);
  };

  const closeHelpModal = () => {
    setIsHelpModalOpen(false);
  };

  const openStatisticModal = () => {
    setIsStatisticHelpModalOpen(true);
  };

  const closeStatisticModal = () => {
    setIsStatisticHelpModalOpen(false);
  };

  return (
    <div className="flex flex-col bg-[rgba(243,243,243,0.89)] dark:bg-[#262B3C] dark:bg-opacity-89 min-h-screen items-center p-20">
      <div className="px-5 bg-[rgba(243,243,243,1)] rounded-md flex flex-row items-center justify-between w-10/12">
        <button onClick={openHelpModal}>
          <HelpIcon />
        </button>
        <span className="text-textHeader text-40">WORDLE</span>
        <div className="flex flex-row">
          <button onClick={openStatisticModal}>
            <StatisticIcon />
          </button>
          <div className="flex flex-row items-center">
            <input type="checkbox" id="toggle" className="toggle--checkbox" />
            <label htmlFor="toggle" className="toggle--label">
              <span className="toggle--label-background"></span>
            </label>
          </div>
        </div>
      </div>
      <div className="flex-1 flex flex-col items-center justify-center py-5 px-4 mx-auto w-full md:1/3 lg:w-1/2 xl:w-1/3"></div>
      <KeyboardInput />
      <HelpModal isOpen={isHelpModalOpen} onClose={closeHelpModal} />
      <StatisticModal
        isOpen={isStatisticModalOpen}
        onClose={closeStatisticModal}
      />
    </div>
  );
}

export default App;
